mapper.select:
select b.id,c.category_code,c.category_name,b.title,b.publish,b.publish_time ,b.create_user,b.create_user_desc,b.create_time ,b.update_user,b.update_user_desc,b.update_time from t_blog b left join t_category c on b.category_code=c.category_code where 1=1
and b.id=#{reqVo.id}
and b.title like concat ('%', #{reqVo.title}, '%')
and b.publish=#{reqVo.publish}
order by b.id desc
